import React, {useState} from "react";
import './buttonWrapper.css';
import Button from "./button";
import Answer from "./answer";

export default function ButtonWrapper()
{
    var [ans, setAns]=useState("");
    const handleClick=(event)=>{
        var value=event.target.value;
        if(value=="=")
        {
            if(ans!=="0")
            {
                try{
                    setAns(eval(ans));
                }catch(err)
                {
                    setAns("Maths Error");
                }
            }
            
        }
        else if(value=="C")
        {
            setAns("");
        }
        else if(value=="D")
        {
            try{
                setAns(ans.substring(0, ans.length-1));
            }catch(err)
            {
                setAns(ans);
            }
        }
        else
        {
            setAns(ans+=value);
        }
    }
    return(
        <div className="buttonWrapper">
            <Answer value={ans}/>
            <Button val="9" handleClick={handleClick}/>
            <Button val="8" handleClick={handleClick}/>
            <Button val="7" handleClick={handleClick}/>
            <Button val="6" handleClick={handleClick}/>
            <Button val="5" handleClick={handleClick}/>
            <Button val="4" handleClick={handleClick}/>
            <Button val="3" handleClick={handleClick}/>
            <Button val="2" handleClick={handleClick}/>
            <Button val="1" handleClick={handleClick}/>
            <Button val="0" handleClick={handleClick}/>
            <Button val="+" handleClick={handleClick}/>
            <Button val="-" handleClick={handleClick}/>
            <Button val="*" handleClick={handleClick}/>
            <Button val="/" handleClick={handleClick}/>
            <Button val="." handleClick={handleClick}/>
            <Button val="C" handleClick={handleClick}/>
            <Button val="D" handleClick={handleClick}/>
            <Button val="=" handleClick={handleClick}/>
        </div>
    )
}
