import React from "react";
import './main.css';
import Answer from "./answer";
import ButtonWrapper from "./buttonWrapper";

export default function Main()
{
    var [ans, setAns]=useState(0);
    const handleClick=(event)=>{
        const value=event.target.value;
        if(value=="=")
        {
            if(ans!=0)
            {
                var temp=0;
                try{
                    temp=Math.eval(ans);
                }catch(err){
                    setAns("Math Error");
                }
                if(temp===undefined)
                {
                    setAns("Math Error");
                }
            }
        }
        else if(value=="C")
        {
            setAns(0);
        }
        else if(value=="D")
        {
            if(ans!=0)
            {
                var temp=ans;
                temp=temp.substring(0, temp.length-1);
                setAns(temp);
            }
        }
        else
        {
            setAns(ans+=value);
        }
    }
    return(
        <div className="main">
            <Answer value={ans}/>
            <ButtonWrapper />
        </div>
    )
}
