import React, {useState} from "react";
import './buttonWrapper.css';
import Button from "./button";

export default function ButtonWrapper()
{
    const [ans, setAns]=useState(0);
    const value=e.target.value;
    const handleClick=()=>{
        if(value=="=")
        {
            if(ans!=0)
            {
                var temp=0;
                try{
                    temp=Math.eval(ans);
                }catch(err){
                    setAns("Math Error");
                }
                if(temp===undefined)
                {
                    setAns("Math Error");
                }
            }
        }
    }
    return(
        <div className="buttonWrapper">
            <Button val="9"/>
            <Button val="8"/>
            <Button val="7"/>
            <Button val="6"/>
            <Button val="5"/>
            <Button val="4"/>
            <Button val="3"/>
            <Button val="2"/>
            <Button val="1"/>
            <Button val="0"/>
            <Button val="+"/>
            <Button val="-"/>
            <Button val="*"/>
            <Button val="/"/>
            <Button val="**"/>
            <Button val="C"/>
            <Button val="D"/>
        </div>
    )
}
